mapper.select:
        select 

        from t_bus_guaranteefeeinfo gf

        	1=1 

               		and gf.org_name like "%"#{feeInfo.orgName}"%" 


               		and gf.account_name like "%"#{feeInfo.accountName}"%" 


               		and gf.certificate_no like "%"#{feeInfo.certificateNo}"%" 


               		and gf.mobile_no like "%"#{feeInfo.mobileNo}"%" 


               		and gf.status  = #{feeInfo.status} 


               		and gf.pay_way = #{feeInfo.payWay}


               		and gf.payer like "%"#{feeInfo.payer}"%" 


	             	and gf.update_date > #{startDate,jdbcType=VARCHAR}


	             	and gf.update_date < #{endDate,jdbcType=VARCHAR}


	             and gf.org_id IN 
	               #{orgId}



        order by create_date desc

        select 
        count(gf.id)
        from t_bus_guaranteefeeinfo gf

        	1=1 

               		and gf.org_name like "%"#{feeInfo.orgName}"%" 


               		and gf.account_name like "%"#{feeInfo.accountName}"%" 


               		and gf.certificate_no like "%"#{feeInfo.certificateNo}"%" 


               		and gf.mobile_no like "%"#{feeInfo.mobileNo}"%" 


               		and gf.status = #{feeInfo.status} 


               		and gf.pay_way = #{feeInfo.payWay}


               		and gf.payer like "%"#{feeInfo.payer}"%" 


	             	and gf.update_date > #{startDate,jdbcType=VARCHAR}


	             	and gf.update_date < #{endDate,jdbcType=VARCHAR}


	             and gf.org_id IN 
	               #{orgId}
